  var newTitle = "";
  var docElement = this.ownerDocument.documentElement;
  var sep = docElement.getAttribute("titlemenuseparator");

  var docTitle = aBrowser.contentTitle;

  if (!docTitle)
    docTitle = docElement.getAttribute("titledefault");

  var modifier = docElement.getAttribute("titlemodifier");
  if (docTitle) {
    newTitle += docElement.getAttribute("titlepreface");
    newTitle += docTitle;

    // Some title could be so long to push the menu off the edge.
    // Set a length limit and determine the text that will be
    // displayed.
    var titleLimit = 50;
    if (newTitle.length > titleLimit) {
      newTitle = newTitle.substring(0, titleLimit);
      // Add "..." to the end
      newTitle += "\u2026";
    }

    if (navigator.oscpu.indexOf("Mac OS X") != -1)
      modifier = "";
    if (modifier)
      newTitle += sep;
  }
  newTitle += modifier;

/* Disabled (http://bugzilla.songbirdnest.com/show_bug.cgi?id=4274)

  // If location bar is hidden and the URL type supports a host,
  // add the scheme and host to the title to prevent spoofing.
  // XXX https://bugzilla.mozilla.org/show_bug.cgi?id=22183#c239
  // (only for schemes that support a host)
  try {
    if (docElement.getAttribute("chromehidden").indexOf("location") != -1) {
      var uri = this.mURIFixup.createExposableURI(
                  aBrowser.currentURI);
      if (uri.host)
        newTitle = uri.prePath + sep + newTitle;
    }
  } catch (e) {}
*/

  if ("TabView" in window) {
    let groupName = TabView.getActiveGroupName();
    if (groupName)
      newTitle = groupName + sep + newTitle;
  }

  return newTitle;


