}
else if (this.arrowKeysShouldWrap)
  this.moveTabToStart();


var tabPos = this.mCurrentTab._tPos;
if (tabPos > 0) {
  this.moveTabTo(this.mCurrentTab, tabPos - 1);
  this.mCurrentTab.focus();
}
else if (this.arrowKeysShouldWrap)
  this.moveTabToEnd();


var tabPos = this.mCurrentTab._tPos;
if (tabPos > 0) {
  this.moveTabTo(this.mCurrentTab, 0);
  this.mCurrentTab.focus();
}


var tabPos = this.mCurrentTab._tPos;
if (tabPos < this.browsers.length - 1) {
  this.moveTabTo(this.mCurrentTab,
                 this.browsers.length - 1);
  this.mCurrentTab.focus();
}


var direction = window.getComputedStyle(this.parentNode, null).direction;
if ((direction == "ltr" && aEvent.keyCode == KeyEvent.DOM_VK_RIGHT) ||
    (direction == "rtl" && aEvent.keyCode == KeyEvent.DOM_VK_LEFT))
  this.moveTabForward();
else
  this.moveTabBackward();


return Cc["@mozilla.org/browser/sessionstore;1"]
         .getService(Ci.nsISessionStore)
         .duplicateTab(window, aTab);

for (var i = 0; i < this.mPanelContainer.childNodes.length; ++i) {
  var cb = this.getBrowserAtIndex(i);
  cb.attachFormFill();
}

for (var i = 0; i < this.mPanelContainer.childNodes.length; ++i) {
  var cb = this.getBrowserAtIndex(i);
  cb.detachFormFill();
}


// Do a security check for drag n' drop. Make sure the
// source document can load the dragged link.
var sourceDoc = aDragSession.sourceDocument;

if (sourceDoc) {
  // Strip leading and trailing whitespace, then try to
  // create a URI from the dropped string. If that
  // succeeds, we're dropping a URI and we need to do a
  // security check to make sure the source document can
  // load the dropped URI. We don't so much care about
  // creating the real URI here (i.e. encoding differences
  // etc don't matter), we just want to know if aUri
  // really is a URI.

  var uriStr = aUri.replace(/^\s*|\s*$/g, '');
  var uri = null;

  try {
    uri = Components.classes["@mozilla.org/network/io-service;1"]
      .getService(Components.interfaces.nsIIOService)
      .newURI(uriStr, null, null);
  } catch (e) {
  }

  if (uri) {
    // aUri is a URI, do the security check.
    var sourceURI = sourceDoc.documentURI;

    const nsIScriptSecurityManager =
      Components.interfaces.nsIScriptSecurityManager;
    var secMan =
      Components.classes["@mozilla.org/scriptsecuritymanager;1"]
      .getService(nsIScriptSecurityManager);

    try {
      secMan.checkLoadURIStr(sourceURI, uriStr,
                             nsIScriptSecurityManager.STANDARD);
    } catch (e) {
      // Stop event propagation right here.
      aEvent.stopPropagation();

      throw "Drop of " + aUri + " denied.";
    }
  }
}

  if (!aEvent.isTrusted) {
    // Don't let untrusted events mess with tabs.
    return;
  }

  if (aEvent.originalTarget.localName == "input") {
    // Don't process keys for input elements!
    return;
  }

  if (aEvent.altKey)
    return;

  // We need to take care of FAYT-watching as long as the findbar
  // isn't initialized.  The checks on aEvent are copied from
  // _shouldFastFind (see findbar.xml).
  if (!gFindBarInitialized &&
      !(aEvent.ctrlKey || aEvent.metaKey) &&
      !aEvent.getPreventDefault()) {
    let charCode = aEvent.charCode;
    if (charCode) {
      let char = String.fromCharCode(charCode);
      if (char == "'" || char == "/" ||
          Services.prefs.getBoolPref("accessibility.typeaheadfind")) {
        gFindBar._onBrowserKeypress(aEvent);
        return;
      }
    }
  }

  var processKeyEvent = false;
  var isMac = window.navigator.platform.match(/^Mac/) == 'Mac';          
  if (isMac && 'metaKey' in aEvent && aEvent.metaKey) {
    var offset = 1;
    switch (aEvent.charCode) {
    // Fallthrough to handle when the text direction is left-to-right 
    case '}'.charCodeAt(0):
      offset *= -1;
    case '{'.charCodeAt(0):
      if (window.getComputedStyle(this.tabbrowser, null).direction == "ltr")
      offset *= -1;

      this.tabbrowser.mTabContainer.advanceSelectedTab(offset, true);
      aEvent.stopPropagation();
      aEvent.preventDefault();
      return;
    }
    if ('shiftKey' in aEvent && aEvent.shiftKey)
      return;

    processKeyEvent = true;
  }
  else {
    if (('ctrlKey' in aEvent && aEvent.ctrlKey) &&
        !('shiftKey' in aEvent && aEvent.shiftKey) &&
        !('metaKey' in aEvent && aEvent.metaKey)) 
    {
      if (aEvent.keyCode == KeyEvent.DOM_VK_F4 &&
          this.tabbrowser.mTabBox.handleCtrlPageUpDown) {
        this.tabbrowser.removeCurrentTab();

        aEvent.stopPropagation();
        aEvent.preventDefault();
        return;
      }

      processKeyEvent = true;
    }
    else if (!('ctrlKey' in aEvent && aEvent.ctrlKey) &&
             !('shiftKey' in aEvent && aEvent.shiftKey) &&
             !('metaKey' in aEvent && aEvent.metaKey)) {
      /*
       * We have to disable the find bar by eating the key event
       * since there is no programmatic way of telling it to disable
       * itself.
       */
      if ((aEvent.charCode == "/".charCodeAt(0) ||
          aEvent.charCode == "'".charCodeAt(0)) &&
          this.tabbrowser.shouldDisableFindAgainForSelectedTab())
      {
        if (window.gSearchHandler && 
            window.gSearchHandler.getSearchBar) {
          var searchBar = window.gSearchHandler.getSearchBar();
          if (searchBar) {
            searchBar.select();
            searchBar.focus();
          }
        }

        aEvent.stopPropagation();
        aEvent.preventDefault();
        return;
      }
    }
  }

  if (processKeyEvent) {
    if (aEvent.target.localName == "tabbrowser" ||
        aEvent.target.localName == "sb-tabbrowser") {
      switch (aEvent.keyCode) {
        case KeyEvent.DOM_VK_UP:
          this.tabbrowser.moveTabBackward();
          break;
        case KeyEvent.DOM_VK_DOWN:
          this.tabbrowser.moveTabForward();
          break;
        case KeyEvent.DOM_VK_RIGHT:
        case KeyEvent.DOM_VK_LEFT:
          this.tabbrowser.moveTabOver(aEvent);
          break;
        case KeyEvent.DOM_VK_HOME:
          this.tabbrowser.moveTabToStart();
          break;
        case KeyEvent.DOM_VK_END:
          this.tabbrowser.moveTabToEnd();
          break;
        default:
          // Stop the keypress event for the above keyboard
          // shortcuts only.
          return;
      }
      aEvent.stopPropagation();
      aEvent.preventDefault();
    }
  }
}

switch (aEvent.type) {
  case "keypress":
    this._handleKeyEvent(aEvent);
    break;
  case "sizemodechange":
    if (aEvent.target == window) {
      this.mCurrentBrowser.docShellIsActive =
        (window.windowState != window.STATE_MINIMIZED);
    }
    break;
}

implementation.constructor:

this.mCurrentBrowser = this.mPanelContainer.childNodes[0].firstChild.firstChild;
this.mCurrentBrowser.getTabBrowser = function() {
  return document.getBindingParent(this.parentNode);
};
this.mCurrentTab = this.tabContainer.firstChild;
/* capture events on process */
document.addEventListener("keypress", this, true);
window.addEventListener("sizemodechange", this, false);

var uniqueId = "panel" + Date.now();
this.mPanelContainer.childNodes[0].id = uniqueId;
this.mCurrentTab.linkedPanel = uniqueId;
this.mCurrentTab._tPos = 0;
this.mCurrentTab._fullyOpen = true;
this.mCurrentTab.linkedBrowser = this.mCurrentBrowser;

this._mFindbar = document.getAnonymousElementByAttribute(this, "id", "FindToolbar");

// Hook up the event listeners to the first browser
var tabListener = this.mTabProgressListener(this.mCurrentTab, this.mCurrentBrowser, true);
const nsIWebProgress = Components.interfaces.nsIWebProgress;
const filter = Components.classes["@mozilla.org/appshell/component/browser-status-filter;1"]
                         .createInstance(nsIWebProgress);
filter.addProgressListener(tabListener, nsIWebProgress.NOTIFY_ALL);
this.mTabListeners[0] = tabListener;
this.mTabFilters[0] = filter;
this.init();

this.style.backgroundColor =
  Services.prefs.getBoolPref("browser.display.use_system_colors") ?
    "-moz-default-background-color" :
    Services.prefs.getCharPref("browser.display.background_color");

method.body:
if (!this._initialProgressListenerAdded) {
  this._initialProgressListenerAdded = true;
  try {
    this.webProgress.addProgressListener(this.mTabFilters[0], Components.interfaces.nsIWebProgress.NOTIFY_ALL);
  } catch (e) {
    // The binding was constructed too early, need to try this again later. See bug 463384.
    this._initialProgressListenerAdded = false;
  }
}

implementation.destructor:

for (var i = 0; i < this.mTabListeners.length; ++i) {
  let browser = this.getBrowserAtIndex(i);
  if (browser.registeredOpenURI) {
    this._placesAutocomplete.unregisterOpenPage(browser.registeredOpenURI);
    delete browser.registeredOpenURI;
  }
  browser.webProgress.removeProgressListener(this.mTabFilters[i]);
  this.mTabFilters[i].removeProgressListener(this.mTabListeners[i]);
  this.mTabFilters[i] = null;
  this.mTabListeners[i].destroy();
  this.mTabListeners[i] = null;
}
document.removeEventListener("keypress", this, true);
window.removeEventListener("sizemodechange", this, false);

property.getter:

return ({
  self: this,
  childNodes: [null, this.tabContextMenu, this.tabContainer],
  firstChild: { nextSibling: this.tabContextMenu },
  getElementsByAttribute: function (attr, attrValue) {
    if (attr == "anonid" && attrValue == "tabContextMenu")
      return [this.self.tabContextMenu];
    return [];
  },
  // Also support adding event listeners (forward to the tab container)
  addEventListener: function (a,b,c) { this.self.tabContainer.addEventListener(a,b,c); },
  removeEventListener: function (a,b,c) { this.self.tabContainer.removeEventListener(a,b,c); }
});

handlers.handler:

if (!event.isTrusted)
  return;

if (this.tabs.length == 1)
  return;

var tab = this._getTabForContentWindow(event.target);
if (tab) {
  this.removeTab(tab);
  event.preventDefault();
}


if (!event.isTrusted)
  return;

// We're about to open a modal dialog, make sure the opening
// tab is brought to the front.
this.selectedTab = this._getTabForContentWindow(event.target.top);


if (!event.isTrusted)
  return;

var contentWin = event.target.defaultView;
if (contentWin != contentWin.top)
  return;

var tab = this._getTabForContentWindow(contentWin);
var titleChanged = this.setTabTitle(tab);
if (titleChanged && !tab.selected && !tab.hasAttribute("busy"))
  tab.setAttribute("titlechanged", "true");

property.setter:
if (!this.label) {
  if (window.gFindBarInitialized && !window.gFindBar.hidden)
    this.setAttribute("mirror", "true");
  else
    this.removeAttribute("mirror");
}

this.style.minWidth = this.getAttribute("type") == "status" &&
                      this.getAttribute("previoustype") == "status"
                        ? getComputedStyle(this).width : "";

if (val) {
  this.setAttribute("label", val);
  this.removeAttribute("inactive");
} else {
  this.setAttribute("inactive", "true");
}

return val;

method.body:
if (event.type == "findbaropen" &&
    this.label)
  this.setAttribute("mirror", "true");

if (this.hasAttribute("mirror"))
  this.removeAttribute("mirror");
else
  this.setAttribute("mirror", "true");
